async fn list_user_projects(octo: &Octocrab, login: &str) -> Result<Vec<ProjectNode>> {
    let q = r#"
        query($login: String!, $first: Int!) {
          user(login: $login) {
            projectsV2(first: $first) {
              nodes { number title }
            }
          }
        }
    "#;
    let vars = serde_json::json!({ "login": login, "first": 50 });
    let resp: UserProjects = octo
        .graphql(q, vars)
        .await
        .context("GraphQL: list user projectsV2 failed")?;
    let Some(user) = resp.user else { return Ok(vec![]) };
    Ok(user.projects_v2.nodes)
}

