async fn main() -> Result<()> {
    // 環境変数: GITHUB_TOKEN（推奨）, GITHUB_LOGIN（省略可・デフォルト"takeshid"）
    let login = std::env::var("GITHUB_LOGIN").unwrap_or_else(|_| "takeshid".to_string());
    let octo = match std::env::var("GITHUB_TOKEN") {
        Ok(token) => Octocrab::builder().personal_token(token).build()?,
        Err(_) => {
            eprintln!(
                "Warning: no GITHUB_TOKEN set. Public info only; private Projects/Issuesは取得不可の可能性があります。"
            );
            octocrab::instance()
        }
    };

