/// ```
/// assert_eq(parse_type(""), None);
/// assert_eq(parse_type("number"), Some(TypeKind::Number));
/// assert_eq(parse_type("number?"), Some(vec![TypeKind::Number, TypeKind::Nil]));
/// assert_eq(parse_type("number| string"), Some(vec![TypeKind::Number, TypeKind::String]));
/// assert_eq(parse_type("number | string | nil"), Some(vec![TypeKind::Number, TypeKind::String,
/// TypeKind::Nil]));
/// ```
fn parse_type(raw: &str) -> Option<TypeKind> {
    let trimmed = raw.trim();
    if trimmed.is_empty() {
        return None;
    }

    // optional
    if let Some(stripped) = trimmed.strip_suffix('?') {
        let base_type = parse_type(stripped.trim())?;
        return Some(make_union(vec![base_type, TypeKind::Nil]));
    }

    // union
    if trimmed.contains('|') {
        let mut members = Vec::new();
        for part in trimmed.split('|').map(str::trim).filter(|p| !p.is_empty()) {
            members.push(parse_type(part)?);
        }
        return Some(make_union(members));
    }

    // array
    if let Some(stripped) = trimmed.strip_suffix("[]") {
        let base_type = parse_type(stripped.trim())?;
        return Some(TypeKind::Array(Box::new(base_type)));
    }

    // function
    // if let Some(stripped) = trimmed.strip_prefix("fun") {
    //     let pos_left_paren = trimmed.find('(').expect("Missing left paren");
    //     let pos_right_paren = trimmed.find(')').expect("Missing right paren");
    //     let mut params = Vec::new();
    //     for part in trimmed.split(',').map(str::trim).filter(|p| !p.starts_with(')'))
    //     let base_type = parse_type(stripped.trim())?;
    //     return Some(TypeKind::Array(Box::new(base_type)));
    // }

    parse_atomic_type(trimmed)
}

fn parse_atomic_type(raw: &str) -> Option<TypeKind> {
    if raw.starts_with('"') || raw.starts_with('\'') {
        return Some(TypeKind::String);
    }

    let lower = raw.to_ascii_lowercase();

    match lower.as_str() {
        "nil" => Some(TypeKind::Nil),
        "boolean" | "bool" => Some(TypeKind::Boolean),
        "string" => Some(TypeKind::String),
        "number" => Some(TypeKind::Number),
        "integer" | "int" => Some(TypeKind::Number),
        "table" => Some(TypeKind::Table),
        "function" | "fun" => Some(TypeKind::Function),
        "thread" => Some(TypeKind::Thread),
        "any" => None,
        _ => Some(TypeKind::Custom(raw.to_string())),
    }
}

fn make_union(types: Vec<TypeKind>) -> TypeKind {
    let mut flat: Vec<TypeKind> = Vec::new();
    for ty in types {
        match ty {
            TypeKind::Union(inner) => flat.extend(inner),
            other => flat.push(other),
        }
    }
    // Todo: Should decision ordering
    flat.sort_by_key(|a| a.to_string());
    flat.dedup_by(|a, b| a.matches(b));

    if flat.len() == 1 {
        flat.into_iter().next().unwrap()
    } else {
        TypeKind::Union(flat)
    }
}

fn parse_class_declaration(line: &str) -> Option<ClassDeclaration> {
    let rest = line.strip_prefix("---@class")?.trim();
    let (rest, exact) = if let Some(remaining) = rest.strip_prefix("(exact)") {
        (remaining.trim(), true)
    } else {
        (rest, false)
    };

    let mut parts = rest.splitn(2, ':');
    let name_part = parts.next()?.trim();
    if name_part.is_empty() {
        return None;
    }

    let parent = parts
        .next()
        .and_then(|value| value.split_whitespace().next())
        .map(|value| value.trim().to_string())
        .filter(|value| !value.is_empty());

    Some(ClassDeclaration {
        name: name_part.to_string(),
        exact,
        parent,
    })
