        let result = run_type_check(
            r#"
            ---@return number
            local function value()
                return 42
            end
            "#,
            .unindent(),
        );

        assert!(result.diagnostics.is_empty());
    }

    #[test]
    fn enum_annotation_treated_as_string() {
        let result = run_type_check(
            r#"
            ---@enum Mode
            ---@field Immediate '"immediate"'
            ---@field Deferred '"deferred"'

            ---@param mode Mode
            local function set_mode(mode)
                mode = "immediate"
            end
            "#,
            .unindent(),
        );

        assert!(result.diagnostics.is_empty());
    }
