#[derive(Debug)]
pub struct TypuaLanguageServer {
    client: Client,
    _root: PathBuf,
    _config: Arc<crate::config::Config>,
    documents: RwLock<HashMap<Url, DocumentState>>,
}

#[derive(Debug, Clone, PartialEq, PartialOrd, Eq, Hash)]
pub struct DocumentPosition {
    pub row: usize,
    pub col: usize,
}

#[derive(Debug, Clone)]
struct DocumentState {
    text: String,
    types: HashMap<DocumentPosition, TypeInfo>,
}

impl TypuaLanguageServer {
    pub fn new(client: Client, options: LspOptions) -> Self {
        Self {
            client,
            _root: options.root,
            _config: Arc::new(options.config),
            documents: RwLock::new(HashMap::new()),
        }
    }

    async fn update_document(&self, uri: Url, text: String) {
        let (diagnostics, types) = self.analyze_document(&uri, &text);

        {
            let mut documents = self.documents.write().await;
            documents.insert(
                uri.clone(),
                DocumentState {
                    text: text.clone(),
                    types,
                },
            );
        }

        self.client
            .publish_diagnostics(uri, diagnostics, None)
            .await;
    }

    async fn remove_document(&self, uri: &Url) {
        {
            let mut documents = self.documents.write().await;
            documents.remove(uri);
        }
        self.client
            .publish_diagnostics(uri.clone(), Vec::new(), None)
            .await;
    }

    fn apply_change(text: &mut String, change: TextDocumentContentChangeEvent) {
        if change.range.is_none() {
            *text = change.text;
            return;
        }

        // TextDocumentSyncKind::FULL guarantees full content updates.
        *text = change.text;
    }

