{"relative_path":"src/typechecker/checker.rs","name_path":"check_ast","body":"
pub fn check_ast_with_registry(
    path: &Path,
    source: &str,
    ast: &ast::Ast,
    workspace_registry: Option<&TypeRegistry>,
) -> CheckResult {
    let (annotations, local_registry) = AnnotationIndex::from_source(source);
    let registry = if let Some(global) = workspace_registry {
        let mut combined = global.clone();
        combined.extend(&local_registry);
        combined
    } else {
        local_registry
    };

    TypeChecker::new(path, annotations, registry).check(ast)
}
"}

{"relative_path":"src/lsp.rs","name_path":"impl TypuaLanguageServer/apply_change","body":"
    fn collect_workspace_registry(&self, current: &Path) -> TypeRegistry {
        let mut registry = TypeRegistry::default();
        match workspace::collect_source_files(self._root.as_path(), &self._config) {
            Ok(files) => {
                for path in files {
                    if path == current {
                        continue;
                    }

                    match fs::read_to_string(&path) {
                        Ok(source) => {
                            let (_, file_registry) = AnnotationIndex::from_source(&source);
                            registry.extend(&file_registry);
                        }
                        Err(error) => {
                            event!(
                                Level::WARN,
                                ?path,
                                ?error,
                                "failed to read workspace file when collecting registry"
                            );
                        }
                    }
                }
            }
            Err(error) => {
                event!(
                    Level::WARN,
                    ?error,
                    "failed to collect workspace files for registry"
                );
            }
        }

        registry
    }
"}

{"relative_path":"src/lsp.rs","name_path":"impl TypuaLanguageServer/analyze_document","body":"fn analyze_document(
        &self,
        uri: &Url,
        text: &str,
    ) -> (Vec<LspDiagnostic>, HashMap<DocumentPosition, TypeInfo>) {
        match full_moon::parse(text) {
            Ok(ast) => {
                let path = uri_to_path(uri);
                let workspace_registry = self.collect_workspace_registry(path.as_path());
                let result = checker::check_ast_with_registry(&path, text, &ast, Some(&workspace_registry));
                let diagnostics = result
                    .diagnostics
                    .into_iter()
                    .map(convert_checker_diagnostic)
                    .collect();
                (diagnostics, result.type_map)
            }
            Err(errors) => (
                errors.into_iter().map(convert_error).collect(),
                HashMap::new(),
            ),
        }
    }"}

