   318	        // mapping is not ready until `AnalysisHost::apply_changes` has been called.
   319	        let mut modified_ratoml_files: FxHashMap<FileId, (ChangeKind, vfs::VfsPath)> =
   320	            FxHashMap::default();
   321	
   322	        let mut change = ChangeWithProcMacros::default();
   323	        let mut guard = self.vfs.write();
   324	        let changed_files = guard.0.take_changes();
   325	        if changed_files.is_empty() {
   326	            return false;
   327	        }
   328	
   329	        let (change, modified_rust_files, workspace_structure_change) =
   330	            self.cancellation_pool.scoped(|s| {
   331	                // start cancellation in parallel, this will kick off lru eviction
   332	                // allowing us to do meaningful work while waiting
   333	                let analysis_host = AssertUnwindSafe(&mut self.analysis_host);
