    }
}

impl fmt::Display for Diagnostic {
    fn fmt(&self, f: &mut fmt::Formatter<'_>) -> fmt::Result {
        match &self.range {
            Some(range) => write!(
                f,
                "{}:{}:{}: {}",
                self.path.display(),
                range.start.line,
                range.start.character,
                self.message
            ),
            None => write!(f, "{}: {}", self.path.display(), self.message),
        }
    }
