            let line = assign
                .variables()
                .pairs()
                .next()
                .and_then(|pair| pair.value().start_position())
                .map(|pos| pos.line())
                .unwrap_or(0);
            let annotations_for_line = if line > 0 {
                annotations.line_annotations(line)
            } else {
                Vec::new()
            };
            let class_hints = if line > 0 {
                annotations.line_class_hints(line)
            } else {
