                .expressions()
                .pairs()
                .map(|pair| to_expr(pair.value(), annotations))
                .collect();
            let body = to_block(generic_for.block(), annotations);
            Stmt::GenericFor(GenericForStmt {
                names,
                generators,
                body,
                range: token_range(generic_for),
            })
        }
