   429	            '(' => depth_paren += 1,
   430	            ')' => depth_paren -= 1,
   431	            '{' => depth_brace += 1,
   432	            '}' => depth_brace -= 1,
   433	            '[' => depth_bracket += 1,
   434	            ']' => depth_bracket -= 1,
   435	            '<' => depth_angle += 1,
   436	            '>' => depth_angle -= 1,
