    29	#[command(name = "typua", version, about = "A Lua type checker and LSP server")]
    30	struct Cli {
    31	    /// Path to an explicit configuration file.
    32	    #[arg(short, long, global = true, value_name = "CONFIG")]
    33	    config: Option<PathBuf>,
    34	    #[command(subcommand)]
    35	    command: Subcommands,
    36	}
    37	
    38	#[derive(Subcommand, Debug)]
    39	enum Subcommands {
    40	    /// Run the type checker over a path
    41	    Check {
    42	        /// Path to a file or directory containing Lua sources
    43	        path: PathBuf,
    44	    },
    45	    /// Start the Typua language server
    46	    Lsp,
    47	}
    48	
    49	pub fn parse() -> Result<Command> {
    50	    let cli = Cli::parse();
    51	    let cwd = std::env::current_dir().map_err(|source| TypuaError::CurrentDir { source })?;
    52	    let config = load_config(&cwd, cli.config.as_ref())?;
    53	
    54	    let command = match cli.command {
    55	        Subcommands::Check { path } => Command::Check(CheckOptions {
    56	            target: path,
    57	            config,
    58	        }),
    59	        Subcommands::Lsp => Command::Lsp(LspOptions { root: cwd, config }),
    60	    };
    61	
    62	    Ok(command)
    63	}
    64	
    65	fn load_config(cwd: &Path, override_path: Option<&PathBuf>) -> Result<Config> {
    66	    if let Some(path) = override_path {
    67	        let resolved = if path.is_absolute() {
    68	            path.clone()
    69	        } else {
    70	            cwd.join(path)
    71	        };
    72	        Config::load_from_file(resolved)
    73	    } else {
    74	        Config::load_from_dir(cwd)
    75	    }
    76	}
