        return Err(TypuaError::UnsupportedTarget {
            path: target.to_path_buf(),
        });
    }

    let root = target
        .canonicalize()
        .unwrap_or_else(|_| target.to_path_buf());
    let mut files = BTreeSet::new();
    // for pattern in &config.runtime.path {
