   130	fn collect_path(path: &Path, files: &mut BTreeSet<PathBuf>) -> Result<()> {
   131	    let metadata = fs::metadata(path).map_err(|source| TypuaError::Metadata {
   132	        path: path.to_path_buf(),
   133	        source,
   134	    })?;
   135	    if metadata.is_dir() {
   136	        collect_from_directory(path, files)?;
   137	    } else if metadata.is_file() {
   138	        push_if_lua(path, files);
   139	    }
   140	    Ok(())
   141	}
   142	
   143	fn collect_from_directory(root: &Path, files: &mut BTreeSet<PathBuf>) -> Result<()> {
   144	    for entry in WalkDir::new(root) {
   145	        let entry = entry.map_err(|source| TypuaError::WalkDir {
   146	            path: root.to_path_buf(),
   147	            source,
   148	        })?;
   149	        if entry.file_type().is_file() {
   150	            push_if_lua(entry.path(), files);
   151	        }
   152	    }
   153	    Ok(())
   154	}
   155	
   156	fn push_if_lua(path: &Path, files: &mut BTreeSet<PathBuf>) {
   157	    if path
   158	        .extension()
   159	        .is_some_and(|ext| ext.eq_ignore_ascii_case("lua"))
   160	    {
   161	        files.insert(canonicalize_path(path));
   162	    }
   163	}
   164	
   165	fn canonicalize_path(path: &Path) -> PathBuf {
   166	    path.canonicalize().unwrap_or_else(|_| path.to_path_buf())
   167	}
   168	
   169	fn workspace_base(target: &Path) -> PathBuf {
   170	    if target.is_dir() {
   171	        canonicalize_path(target)
   172	    } else {
   173	        target
   174	            .parent()
   175	            .map(canonicalize_path)
   176	            .unwrap_or_else(|| canonicalize_path(target))
   177	    }
   178	}
