#![allow(dead_code)]

use std::path::PathBuf;

use crate::types::TypeId;

use super::import::ImportDecl;
use super::nodes::{NodeId, NodeType, TypedNode};
use super::{FullMoonNodeId, TypedAst};

#[derive(Debug)]
pub struct TypedAstBuilder {
    ast: TypedAst,
}

impl TypedAstBuilder {
    pub fn new(file_path: PathBuf) -> Self {
        Self {
            ast: TypedAst {
                file_path,
                ..TypedAst::default()
            },
        }
    }

    pub fn push_node(&mut self, node_type: NodeType) -> NodeId {
        let id = NodeId::new(self.ast.nodes.len());
        self.ast.nodes.push(TypedNode::new(id, node_type));
        id
    }

    pub fn set_parent(&mut self, child: NodeId, parent: Option<NodeId>) {
        if let Some(node) = self.ast.nodes.get_mut(child.get()) {
            node.parent = parent;
        }
        if let Some(parent_id) = parent {
            if let Some(parent_node) = self.ast.nodes.get_mut(parent_id.get()) {
                if !parent_node.children.contains(&child) {
                    parent_node.children.push(child);
                }
            }
        }
    }

    pub fn assign_type(&mut self, node: NodeId, ty: TypeId) {
        self.ast.type_annotations.insert(node, ty);
    }

    pub fn add_import(&mut self, import: ImportDecl) {
        self.ast.imports.push(import);
    }

    pub fn map_full_moon_id(&mut self, node: NodeId, full_moon_id: FullMoonNodeId) {
        self.ast.full_moon_map.insert(node, full_moon_id);
    }

    pub fn add_root(&mut self, node: NodeId) {
        self.ast.root_ids.push(node);
    }

    pub fn finish(self) -> TypedAst {
        self.ast
    }
}
EOF
