#[derive(Clone, Debug, PartialEq, Eq)]
pub struct PathsConfig {
    pub root: PathBuf,
    pub library_paths: Vec<PathBuf>,
}

impl PathsConfig {
    fn from_section(section: &PathsSection, base_dir: Option<&PathBuf>) -> Result<Self, ConfigError> {
        let root = section
            .root
            .clone()
            .map(PathBuf::from)
            .unwrap_or_else(|| PathBuf::from("./src"));
        let rooted = resolve_path(&root, base_dir);
        let library_paths = section
            .library_paths
            .clone()
            .unwrap_or_default()
            .into_iter()
            .map(PathBuf::from)
            .map(|path| resolve_path(&path, base_dir))
            .collect();
        Ok(Self {
            root: rooted,
            library_paths,
        })
    }
}

