    pub output_format: DiagnosticOutputFormat,
}

impl DiagnosticsConfig {
    fn from_section(section: &DiagnosticsSection) -> Result<Self, ConfigError> {
        let overrides = section
            .severity_levels
            .clone()
            .unwrap_or_default()
            .into_iter()
            .map(|(key, value)| {
                let severity = Severity::try_from(&*value)?;
                Ok((key, severity))
            })
            .collect::<Result<BTreeMap<_, _>, ConfigError>>()?;
        let output_format = section
            .output_format
            .as_deref()
            .map(DiagnosticOutputFormat::try_from_str)
            .transpose()? 
            .unwrap_or_default();
        Ok(Self {
            severity_overrides: overrides,
            output_format,
        })
    }
}

