}

impl DiagnosticOutputFormat {
    pub fn as_str(&self) -> &'static str {
        match self {
            DiagnosticOutputFormat::Human => "human",
            DiagnosticOutputFormat::Json => "json",
            DiagnosticOutputFormat::Checkstyle => "checkstyle",
        }
    }

    fn try_from_str(value: &str) -> Result<Self, ConfigError> {
        match value.to_ascii_lowercase().as_str() {
            "human" => Ok(DiagnosticOutputFormat::Human),
            "json" => Ok(DiagnosticOutputFormat::Json),
            "checkstyle" => Ok(DiagnosticOutputFormat::Checkstyle),
            _ => Err(ConfigError::InvalidDiagnosticFormat(value.to_string())),
        }
    }
}

impl Default for DiagnosticOutputFormat {
    fn default() -> Self {
        DiagnosticOutputFormat::Human
    }
}

#[derive(Debug, Error)]
pub enum ConfigError {
    #[error("I/O error while reading config: {0}")]
    Io(#[from] std::io::Error),
    #[error("failed to parse TOML: {0}")]
    Toml(#[from] toml::de::Error),
    #[error("invalid Lua version: {0}")]
    InvalidLuaVersion(String),
    #[error("invalid diagnostic format: {0}")]
    InvalidDiagnosticFormat(String),
    #[error("invalid severity level: {0}")]
    InvalidSeverity(String),
}

fn resolve_path(path: &PathBuf, base_dir: Option<&PathBuf>) -> PathBuf {
    if path.is_absolute() {
        return path.clone();
    }
    if let Some(base) = base_dir {
        base.join(path)
    } else {
        path.clone()
    }
}

impl Severity {
    fn try_from(value: &str) -> Result<Self, ConfigError> {
        match value.to_ascii_lowercase().as_str() {
            "error" => Ok(Severity::Error),
            "warning" => Ok(Severity::Warning),
            "information" | "info" => Ok(Severity::Information),
            "hint" => Ok(Severity::Hint),
            other => Err(ConfigError::InvalidSeverity(other.to_string())),
        }
    }
}

