#![allow(dead_code)]

use std::path::{Path, PathBuf};

use thiserror::Error;

use crate::annotation::AnnotationParser;
use crate::ast::TypedAst;
use crate::config::{Config, ConfigError};
use crate::crawler::FileCrawler;
use crate::diagnostics::DiagnosticsReport;
use crate::environment::TypeEnvironment;
use crate::resolver::ModuleResolver;

pub type CheckResult<T> = Result<T, CheckerError>;

#[derive(Debug, Error)]
pub enum CheckerError {
    #[error("configuration error: {0}")]
    Config(#[from] ConfigError),
    #[error("I/O error: {0}")]
    Io(#[from] std::io::Error),
    #[error("internal error: {0}")]
    Internal(String),
}

#[derive(Debug)]
pub struct TypeChecker {
    config: Config,
    annotation_parser: AnnotationParser,
}

impl TypeChecker {
    pub fn new(config: Config) -> Self {
        Self {
            config,
            annotation_parser: AnnotationParser::default(),
        }
    }

    pub fn config(&self) -> &Config {
        &self.config
    }

    pub fn check_source<P>(&self, path: P, _source: &str) -> CheckResult<DiagnosticsReport>
    where
        P: AsRef<Path>,
    {
        let mut diagnostics = DiagnosticsReport::new();
        let file_path = path.as_ref().to_path_buf();
        let mut typed_ast = TypedAst::new(file_path);
        let mut environment = TypeEnvironment::new();
        let mut resolver = ModuleResolver::new();
        self.annotation_parser
            .parse_file(path.as_ref(), &mut typed_ast, &mut diagnostics);
        // TODO: full_moonを用いたAST構築・型推論
        let _ = (&mut typed_ast, &mut environment, &mut resolver); // satisfy unused warnings
        Ok(diagnostics)
    }

    pub fn check_directory(&self) -> CheckResult<DiagnosticsReport> {
        let crawler = FileCrawler::new(self.config.crawler().clone());
        let files = crawler.collect();
        let mut diagnostics = DiagnosticsReport::new();
        for file in files {
            let source = std::fs::read_to_string(&file)?;
            let file_diagnostics = self.check_source(file, &source)?;
            diagnostics.extend(file_diagnostics.into_iter());
        }
        Ok(diagnostics)
    }
}

impl Default for TypeChecker {
    fn default() -> Self {
        Self::new(Config::default())
    }
}
