use thiserror::Error;

use crate::diagnostics::Severity;

#[derive(Clone, Debug, PartialEq, Eq)]
pub struct Config {
    project: ProjectConfig,
    paths: PathsConfig,
    crawler: CrawlerConfig,
    checker: CheckerConfig,
    diagnostics: DiagnosticsConfig,
    annotations: AnnotationsConfig,
}

impl Config {
    pub fn load_from_file<P: AsRef<Path>>(path: P) -> Result<Self, ConfigError> {
        let path = path.as_ref();
        let text = fs::read_to_string(path)?;
        let base_dir = path.parent().map(Path::to_path_buf);
        Self::from_toml_str(&text, base_dir.as_ref())
    }

    pub fn from_toml_str(src: &str, base_dir: Option<&PathBuf>) -> Result<Self, ConfigError> {
        let raw: RawConfig = toml::from_str(src)?;
        Self::from_raw(raw, base_dir)
    }

    pub fn from_raw(raw: RawConfig, base_dir: Option<&PathBuf>) -> Result<Self, ConfigError> {
        let project = ProjectConfig::from_section(&raw.project.unwrap_or_default())?;
        let paths = PathsConfig::from_section(&raw.paths.unwrap_or_default(), base_dir)?;
        let crawler = CrawlerConfig::from_section(&raw.crawler.unwrap_or_default());
        let checker = CheckerConfig::from_section(&raw.checker.unwrap_or_default());
        let diagnostics = DiagnosticsConfig::from_section(&raw.diagnostics.unwrap_or_default())?;
        let annotations = AnnotationsConfig::from_section(&raw.annotations.unwrap_or_default());

        Ok(Self {
            project,
            paths,
            crawler,
            checker,
            diagnostics,
            annotations,
        })
    }

    pub fn project(&self) -> &ProjectConfig {
        &self.project
    }

    pub fn paths(&self) -> &PathsConfig {
        &self.paths
    }

    pub fn crawler(&self) -> &CrawlerConfig {
        &self.crawler
    }

    pub fn checker(&self) -> &CheckerConfig {
        &self.checker
    }

    pub fn diagnostics(&self) -> &DiagnosticsConfig {
        &self.diagnostics
    }

    pub fn annotations(&self) -> &AnnotationsConfig {
        &self.annotations
    }
}

impl Default for Config {
    fn default() -> Self {
        Self {
            project: ProjectConfig::default(),
            paths: PathsConfig::default(),
            crawler: CrawlerConfig::default(),
            checker: CheckerConfig::default(),
            diagnostics: DiagnosticsConfig::default(),
            annotations: AnnotationsConfig::default(),
