//! Luaファイルの探索を担当するモジュール。

use std::path::{Path, PathBuf};

use globset::{Glob, GlobSet, GlobSetBuilder};
use ignore::WalkBuilder;
use thiserror::Error;

use crate::config::CrawlerConfig;

#[derive(Debug)]
pub struct FileCrawler {
    config: CrawlerConfig,
}

impl FileCrawler {
    pub fn new(config: CrawlerConfig) -> Self {
        Self { config }
    }

    pub fn config(&self) -> &CrawlerConfig {
        &self.config
    }

    pub fn collect<P>(&self, root: P) -> Result<Vec<PathBuf>, CrawlerError>
    where
        P: AsRef<Path>,
    {
        let root_path = root.as_ref();
        let exclude_set = build_exclude_set(&self.config)?;

        let mut builder = WalkBuilder::new(root_path);
        builder.follow_links(self.config.follow_symlinks);
        if !self.config.respect_gitignore {
            builder.git_ignore(false);
            builder.git_global(false);
            builder.git_exclude(false);
        }

        let mut files = Vec::new();
        for entry in builder.build() {
            let entry = entry?;
            let file_type = match entry.file_type() {
                Some(ft) => ft,
                None => continue,
            };
            if !file_type.is_file() {
                continue;
            }

            if !is_lua_file(entry.path()) {
                continue;
            }

            if let Some(ref globset) = exclude_set {
                let relative = entry
                    .path()
                    .strip_prefix(root_path)
                    .unwrap_or_else(|_| entry.path());
                if globset.is_match(relative) {
                    continue;
                }
            }

            files.push(entry.into_path());
        }

        files.sort();
        Ok(files)
    }
}

fn build_exclude_set(config: &CrawlerConfig) -> Result<Option<GlobSet>, CrawlerError> {
    if config.exclude_patterns.is_empty() {
        return Ok(None);
    }

    let mut builder = GlobSetBuilder::new();
    for pattern in &config.exclude_patterns {
        let glob = Glob::new(pattern)
            .map_err(|source| CrawlerError::GlobPattern { pattern: pattern.clone(), source })?;
        builder.add(glob);
    }

    builder
        .build()
        .map(Some)
        .map_err(|source| CrawlerError::GlobPattern {
            pattern: "<builder>".to_string(),
            source,
        })
}

fn is_lua_file(path: &Path) -> bool {
    matches!(
        path.extension().and_then(|ext| ext.to_str()),
        Some(ext) if ext.eq_ignore_ascii_case("lua")
    )
}

#[derive(Debug, Error)]
pub enum CrawlerError {
    #[error("glob patternエラー: `{pattern}` -> {source}")]
    GlobPattern {
        pattern: String,
        #[source]
        source: globset::Error,
    },
    #[error("ウォーク処理エラー: {0}")]
    Walk(#[from] ignore::Error),
}

#[cfg(test)]
mod tests {
    use std::fs::{self, File};
    use std::io::Write;

    use tempfile::tempdir;

    use super::*;

    #[test]
    fn collects_lua_files_with_excludes_and_gitignore() {
        let root = tempdir().unwrap();
        let root_path = root.path();

        // ディレクトリとファイルを作成
        let module_dir = root_path.join("module");
        fs::create_dir_all(&module_dir).unwrap();
        File::create(root_path.join("main.lua")).unwrap();
        File::create(module_dir.join("init.lua")).unwrap();
        File::create(root_path.join("notes.txt")).unwrap();

        // 除外対象
        let test_dir = root_path.join("tests");
        fs::create_dir_all(&test_dir).unwrap();
        File::create(test_dir.join("spec.lua")).unwrap();

        // .gitignoreで無視するファイル
        let mut gitignore = File::create(root_path.join(".gitignore")).unwrap();
        writeln!(gitignore, "ignored.lua").unwrap();
        File::create(root_path.join("ignored.lua")).unwrap();

        let config = CrawlerConfig {
