{"output":"use chrono::{DateTime, Utc};
use serde::{Deserialize, Serialize};
use std::collections::HashMap;

#[derive(Debug, Clone, Default, Serialize, Deserialize)]
pub struct TokenUsage {
    pub input_tokens: u64,
    pub output_tokens: u64,
    pub reasoning_tokens: u64,
    pub cached_tokens: u64,
    pub total: u64,
}

impl TokenUsage {
    pub fn new() -> Self {
        Self::default()
    }

    pub fn add(&mut self, other: &TokenUsage) {
        self.input_tokens += other.input_tokens;
        self.output_tokens += other.output_tokens;
        self.reasoning_tokens += other.reasoning_tokens;
        self.cached_tokens += other.cached_tokens;
        self.total += other.total;
    }
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ToolUsage {
    pub tool_name: String,
    pub call_count: u32,
    pub tokens: TokenUsage,
    pub arguments: Vec<String>,
}

impl ToolUsage {
    pub fn new(tool_name: String) -> Self {
        Self {
            tool_name,
            call_count: 0,
            tokens: TokenUsage::new(),
            arguments: Vec::new(),
        }
    }

    pub fn add_call(&mut self, tokens: &TokenUsage, arg: Option<String>) {
        self.call_count += 1;
        self.tokens.add(tokens);
        if let Some(arg) = arg {
            self.arguments.push(arg);
        }
    }
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct BashCommand {
    pub command: String,
    pub description: Option<String>,
    pub exit_code: Option<i32>,
    pub tokens_used: TokenUsage,
    pub timestamp: Option<DateTime<Utc>>,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SessionAnalysis {
    pub project_name: String,
    pub session_date: DateTime<Utc>,
    pub session_type: SessionType,

    pub token_summary: TokenUsage,
    pub tokens_by_tool: HashMap<String, TokenUsage>,

    pub tool_summary: HashMap<String, ToolUsage>,

    pub bash_commands: Vec<BashCommand>,
}

impl SessionAnalysis {
    pub fn new(project_name: String, session_type: SessionType) -> Self {
        Self {
            project_name,
            session_date: Utc::now(),
            session_type,
            token_summary: TokenUsage::new(),
            tokens_by_tool: HashMap::new(),
            tool_summary: HashMap::new(),
            bash_commands: Vec::new(),
        }
    }

    pub fn add_tool_usage(&mut self, tool_name: &str, tokens: &TokenUsage, arg: Option<String>) {
        self.token_summary.add(tokens);

        self.tokens_by_tool
            .entry(tool_name.to_string())
            .or_insert_with(TokenUsage::new)
            .add(tokens);

        self.tool_summary
            .entry(tool_name.to_string())
            .or_insert_with(|| ToolUsage::new(tool_name.to_string()))
            .add_call(tokens, arg);
    }

    pub fn add_bash_command(&mut self, cmd: BashCommand) {
        self.bash_commands.push(cmd);
    }
}

#[derive(Debug, Clone, Copy, PartialEq, Eq, Serialize, Deserialize)]
pub enum SessionType {
    Codex,
    ClaudeCode,
}

#[derive(Debug, Deserialize)]
#[serde(tag = "type")]
pub enum CodexLogEntry {
    #[serde(rename = "session_meta")]
    SessionMeta { payload: CodexSessionMetaPayload },

    #[serde(rename = "response_item")]
    ResponseItem { payload: CodexResponsePayload },

    #[serde(rename = "event_msg")]
    EventMsg { payload: CodexEventPayload },

    #[serde(rename = "turn_context")]
    TurnContext { payload: serde_json::Value },

    #[serde(other)]
    Unknown,
}

#[derive(Debug, Deserialize)]
pub struct CodexSessionMetaPayload {
    pub id: String,
    pub timestamp: String,
    pub cwd: String,
    pub originator: Option<String>,
    pub cli_version: Option<String>,
    pub git: Option<GitInfo>,
}

#[derive(Debug, Deserialize)]
pub struct GitInfo {
    pub commit_hash: Option<String>,
    pub branch: Option<String>,
    pub repository_url: Option<String>,
}

#[derive(Debug, Deserialize)]
#[serde(tag = "type")]
pub enum CodexResponsePayload {
    #[serde(rename = "message")]
    Message {
        role: String,
        content: Vec<serde_json::Value>,
    },

    #[serde(rename = "function_call")]
    FunctionCall {
        name: String,
        arguments: String,
        call_id: String,
    },

    #[serde(rename = "function_call_output")]
    FunctionCallOutput {
        call_id: String,
        output: String,
    },

    #[serde(rename = "reasoning")]
    Reasoning {
        summary: Option<Vec<serde_json::Value>>,
        content: Option<String>,
        encrypted_content: Option<String>,
    },

    #[serde(other)]
    Unknown,
}

#[derive(Debug, Deserialize)]
#[serde(tag = "type")]
pub enum CodexEventPayload {
    #[serde(rename = "token_count")]
    TokenCount {
        info: Option<CodexTokenInfo>,
        rate_limits: Option<serde_json::Value>,
    },

    #[serde(rename = "agent_reasoning")]
    AgentReasoning { text: String },

    #[serde(other)]
    Unknown,
}

