{"output":"use regex::Regex;
use std::collections::HashMap;

use crate::models::{SessionAnalysis, TokenUsage};

#[derive(Debug, Clone, PartialEq, Eq, Hash)]
pub enum BashCategory {
    Git,
    Cargo,
    Npm,
    Pnpm,
    Docker,
    Python,
    Other,
}

impl BashCategory {
    pub fn as_str(&self) -> &str {
        match self {
            BashCategory::Git => "git",
            BashCategory::Cargo => "cargo",
            BashCategory::Npm => "npm",
            BashCategory::Pnpm => "pnpm",
            BashCategory::Docker => "docker",
            BashCategory::Python => "python",
            BashCategory::Other => "other",
        }
    }
}

#[derive(Debug, Clone)]
pub struct BashAnalysis {
    pub category: BashCategory,
    pub command: String,
    pub subcommand: Option<String>,
    pub tokens: TokenUsage,
    pub count: u32,
}

pub fn categorize_bash_command(cmd: &str) -> BashCategory {
    let cmd_lower = cmd.trim().to_lowercase();

    if cmd_lower.starts_with("git ") || cmd_lower == "git" {
        BashCategory::Git
    } else if cmd_lower.starts_with("cargo ") || cmd_lower == "cargo" {
        BashCategory::Cargo
    } else if cmd_lower.starts_with("npm ") || cmd_lower == "npm" {
        BashCategory::Npm
    } else if cmd_lower.starts_with("pnpm ") || cmd_lower == "pnpm" {
        BashCategory::Pnpm
    } else if cmd_lower.starts_with("docker ") || cmd_lower == "docker" {
        BashCategory::Docker
    } else if cmd_lower.starts_with("python ") || cmd_lower.starts_with("python3 ") {
        BashCategory::Python
    } else {
        BashCategory::Other
    }
}

/// Extract subcommand from bash command
pub fn extract_subcommand(cmd: &str, category: &BashCategory) -> Option<String> {
    let re = match category {
        BashCategory::Git => Regex::new(r"^git\s+(\w+)").ok()?,
        BashCategory::Cargo => Regex::new(r"^cargo\s+(\w+)").ok()?,
        BashCategory::Npm => Regex::new(r"^npm\s+(\w+)").ok()?,
        BashCategory::Pnpm => Regex::new(r"^pnpm\s+(\w+)").ok()?,
        BashCategory::Docker => Regex::new(r"^docker\s+(\w+)").ok()?,
        BashCategory::Python => return Some("script".to_string()),
        BashCategory::Other => return None,
    };

    re.captures(cmd.trim())
        .and_then(|caps| caps.get(1))
        .map(|m| m.as_str().to_string())
}

/// Analyze bash commands from session
pub fn analyze_bash_commands(session: &SessionAnalysis) -> HashMap<BashCategory, Vec<BashAnalysis>> {
    let mut results: HashMap<BashCategory, Vec<BashAnalysis>> = HashMap::new();

    for bash_cmd in &session.bash_commands {
        let category = categorize_bash_command(&bash_cmd.command);
        let subcommand = extract_subcommand(&bash_cmd.command, &category);

        let analysis = BashAnalysis {
            category: category.clone(),
            command: bash_cmd.command.clone(),
            subcommand,
            tokens: bash_cmd.tokens_used.clone(),
            count: 1,
        };

        results
            .entry(category)
            .or_insert_with(Vec::new)
            .push(analysis);
    }

    results
}

pub fn aggregate_bash_by_category(
    bash_analyses: &HashMap<BashCategory, Vec<BashAnalysis>>,
) -> HashMap<BashCategory, (u32, TokenUsage)> {
    let mut aggregated = HashMap::new();

    for (category, analyses) in bash_analyses {
        let mut total_tokens = TokenUsage::new();
        let mut total_count = 0;

        for analysis in analyses {
            total_tokens.add(&analysis.tokens);
            total_count += analysis.count;
        }

        aggregated.insert(category.clone(), (total_count, total_tokens));
    }

    aggregated
}

pub fn aggregate_bash_by_subcommand(
    bash_analyses: &HashMap<BashCategory, Vec<BashAnalysis>>,
) -> HashMap<String, (u32, TokenUsage)> {
    let mut aggregated = HashMap::new();

    for (_category, analyses) in bash_analyses {
        for analysis in analyses {
            let key = if let Some(subcmd) = &analysis.subcommand {
                format!("{} {}", analysis.category.as_str(), subcmd)
            } else {
                analysis.category.as_str().to_string()
            };

            let entry = aggregated
                .entry(key)
                .or_insert_with(|| (0, TokenUsage::new()));

            entry.0 += analysis.count;
            entry.1.add(&analysis.tokens);
        }
    }

    aggregated
}

/// Get top N tools by usage
pub fn get_top_tools(
    session: &SessionAnalysis,
    n: usize,
) -> Vec<(String, TokenUsage, f64)> {
    let total = session.token_summary.total as f64;

    let mut tools: Vec<_> = session
        .tokens_by_tool
        .iter()
        .map(|(name, tokens)| {
            let percentage = if total > 0.0 {
                (tokens.total as f64 / total) * 100.0
            } else {
                0.0
            };
            (name.clone(), tokens.clone(), percentage)
        })
        .collect();

    tools.sort_by(|a, b| b.1.total.cmp(&a.1.total));
    tools.into_iter().take(n).collect()
}

#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn test_categorize_bash_command() {
        assert_eq!(categorize_bash_command("git status"), BashCategory::Git);
        assert_eq!(categorize_bash_command("cargo build"), BashCategory::Cargo);
        assert_eq!(categorize_bash_command("npm install"), BashCategory::Npm);
        assert_eq!(categorize_bash_command("pnpm test"), BashCategory::Pnpm);
        assert_eq!(categorize_bash_command("docker ps"), BashCategory::Docker);
        assert_eq!(categorize_bash_command("ls -la"), BashCategory::Other);
    }

    #[test]
    fn test_extract_subcommand() {
        assert_eq!(
            extract_subcommand("git status", &BashCategory::Git),
            Some("status".to_string())
        );
        assert_eq!(
            extract_subcommand("cargo clippy --all", &BashCategory::Cargo),
            Some("clippy".to_string())
        );
        assert_eq!(
            extract_subcommand("npm run dev", &BashCategory::Npm),
            Some("run".to_string())
        );
    }
}
