{"output":"use anyhow::{Context, Result};
use chrono::{DateTime, Utc};
use std::path::Path;

use crate::models::{
    BashCommand, CodexEventPayload, CodexLogEntry, CodexResponsePayload, SessionAnalysis,
    SessionType, TokenUsage,
};

use super::parse_ndjson_file;

pub fn parse_codex_session(path: &Path) -> Result<SessionAnalysis> {
    let entries = parse_ndjson_file(path, |line, line_num| {
        let entry: CodexLogEntry = serde_json::from_str(line)
            .with_context(|| format!("Failed to parse line {} as CodexLogEntry", line_num))?;
        Ok(Some(entry))
    })?;

    let (project_name, session_date) = extract_session_meta(&entries)?;

    let mut analysis = SessionAnalysis::new(project_name, SessionType::Codex);
    analysis.session_date = session_date;

    let mut call_id_to_tool: std::collections::HashMap<String, (String, String)> =
        std::collections::HashMap::new();

    for entry in &entries {
        match entry {
            CodexLogEntry::ResponseItem { payload } => {
                if let CodexResponsePayload::FunctionCall {
                    name,
                    arguments,
                    call_id,
                } = payload
                {
                    let tool_name = normalize_tool_name(name);

                    let arg_summary = extract_argument_summary(&tool_name, arguments);

                    call_id_to_tool
                        .insert(call_id.clone(), (tool_name.clone(), arg_summary.clone()));

                    if tool_name == "Bash" || tool_name == "shell" {
                        if let Some(cmd) = extract_bash_command(arguments) {
                            analysis.add_bash_command(BashCommand {
                                command: cmd,
                                description: Some(arg_summary.clone()),
                                exit_code: None,
                                tokens_used: TokenUsage::new(),
                                timestamp: Some(Utc::now()),
                            });
                        }
                    }
                }
            }
            CodexLogEntry::EventMsg { payload } => {
                if let CodexEventPayload::TokenCount { info, .. } = payload {
                    if let Some(info) = info {
                        if let Some(last_usage) = &info.last_token_usage {
                            let tokens: TokenUsage = last_usage.into();

                            if let Some((tool_name, arg)) = call_id_to_tool.values().last() {
                                analysis.add_tool_usage(tool_name, &tokens, Some(arg.clone()));
                            }
                        }
                    }
                }
            }
            _ => {}
        }
    }

    Ok(analysis)
}

fn extract_session_meta(entries: &[CodexLogEntry]) -> Result<(String, DateTime<Utc>)> {
    for entry in entries {
        if let CodexLogEntry::SessionMeta { payload } = entry {
            let project_name = extract_project_name(&payload.cwd);

            let session_date = chrono::DateTime::parse_from_rfc3339(&payload.timestamp)
                .with_context(|| format!("Failed to parse timestamp: {}", payload.timestamp))?
                .with_timezone(&Utc);

            return Ok((project_name, session_date));
        }
    }

    anyhow::bail!("No session_meta entry found in the log");
}

fn extract_project_name(cwd: &str) -> String {
    std::path::Path::new(cwd)
        .file_name()
        .and_then(|n| n.to_str())
        .unwrap_or("unknown")
        .to_string()
}

/// normalize tool name
/// "sequential-thinking__sequentialthinking" -> "sequential-thinking"
/// "shell" -> "Bash"
fn normalize_tool_name(name: &str) -> String {
    if let Some(base) = name.split("__").next() {
        base.to_string()
    } else if name == "shell" {
        "Bash".to_string()
    } else {
        name.to_string()
    }
}

fn extract_argument_summary(tool_name: &str, arguments: &str) -> String {
    match tool_name {
        "Bash" | "shell" => {
            if let Ok(args) = serde_json::from_str::<serde_json::Value>(arguments) {
                if let Some(cmd) = args.get("command").and_then(|v| v.as_str()) {
                    // コマンドを配列形式で受け取る場合
                    return cmd.to_string();
                } else if let Some(arr) = args.get("command").and_then(|v| v.as_array()) {
                    // ["bash", "-lc", "cat AGENTS.md"] -> "cat AGENTS.md"
                    if let Some(last) = arr.last().and_then(|v| v.as_str()) {
                        return last.to_string();
                    }
                }
            }
            "unknown".to_string()
        }
        "sequential-thinking" => "Planning".to_string(),
        _ => {
            // Truncate arguments to 50 characters (not bytes)
            let char_count = arguments.chars().count();
            if char_count > 50 {
                let truncated: String = arguments.chars().take(50).collect();
                format!("{}...", truncated)
            } else {
                arguments.to_string()
            }
        }
    }
}

fn extract_bash_command(arguments: &str) -> Option<String> {
    if let Ok(args) = serde_json::from_str::<serde_json::Value>(arguments) {
        if let Some(cmd) = args.get("command").and_then(|v| v.as_str()) {
            return Some(cmd.to_string());
        } else if let Some(arr) = args.get("command").and_then(|v| v.as_array()) {
            if let Some(last) = arr.last().and_then(|v| v.as_str()) {
                return Some(last.to_string());
            }
        }
    }
    None
}

#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn test_normalize_tool_name() {
        assert_eq!(
            normalize_tool_name("sequential-thinking__sequentialthinking"),
            "sequential-thinking"
        );
        assert_eq!(normalize_tool_name("shell"), "Bash");
        assert_eq!(normalize_tool_name("serena__read_file"), "serena");
    }

    #[test]
    fn test_extract_project_name() {
        assert_eq!(
            extract_project_name("/home/user/projects/myproject"),
            "myproject"
        );
        assert_eq!(
            extract_project_name("/home/user/ex_prog/ex_rust/typua"),
            "typua"
        );
    }

    #[test]
    fn test_extract_bash_command() {
        let args =
            r#"{"command":["bash","-lc","cat AGENTS.md"],"workdir":".","timeout_ms":120000}"#;
        assert_eq!(
            extract_bash_command(args),
            Some("cat AGENTS.md".to_string())
        );

        let args2 = r#"{"command":"git status"}"#;
        assert_eq!(extract_bash_command(args2), Some("git status".to_string()));
    }
}
