{"output":"use anyhow::{Context, Result};
use chrono::{DateTime, Utc};
use std::path::Path;

use crate::models::{
    BashCommand, ClaudeCodeLogEntry, ClaudeContent, SessionAnalysis, SessionType, TokenUsage,
};

use super::parse_ndjson_file;

pub fn parse_claudecode_session(path: &Path) -> Result<SessionAnalysis> {
    let entries = parse_ndjson_file(path, |line, line_num| {
        let entry: ClaudeCodeLogEntry = serde_json::from_str(line)
            .with_context(|| format!("Failed to parse line {} as ClaudeCodeLogEntry", line_num))?;
        Ok(Some(entry))
    })?;

    let (project_name, session_date) = extract_session_meta(&entries, path)?;

    let mut analysis = SessionAnalysis::new(project_name, SessionType::ClaudeCode);
    analysis.session_date = session_date;

    for entry in &entries {
        if let ClaudeCodeLogEntry::Assistant { message, .. } = entry {
            let tokens = if let Some(usage) = &message.usage {
                usage.into()
            } else {
                TokenUsage::new()
            };

            for content in &message.content {
                match content {
                    ClaudeContent::ToolUse { name, input, .. } => {
                        let tool_name = name.clone();
                        let arg_summary = extract_argument_summary(&tool_name, input);

                        if tool_name == "Bash" {
                            if let Some(cmd) = extract_bash_command(input) {
                                analysis.add_bash_command(BashCommand {
                                    command: cmd.clone(),
                                    description: Some(arg_summary.clone()),
                                    exit_code: None,
                                    tokens_used: tokens.clone(),
                                    timestamp: Some(Utc::now()),
                                });
                            }
                        }

                        analysis.add_tool_usage(&tool_name, &tokens, Some(arg_summary));
                    }
                    ClaudeContent::Text { .. } => {
                        if message
                            .content
                            .iter()
                            .all(|c| matches!(c, ClaudeContent::Text { .. }))
                        {
                            analysis.add_tool_usage("TextResponse", &tokens, None);
                        }
                    }
                    _ => {}
                }
            }
        }
    }

    Ok(analysis)
}

fn extract_session_meta(
    entries: &[ClaudeCodeLogEntry],
    path: &Path,
) -> Result<(String, DateTime<Utc>)> {
    for entry in entries {
        match entry {
            ClaudeCodeLogEntry::User { cwd, timestamp, .. } => {
                let project_name = if let Some(cwd) = cwd {
                    extract_project_name(cwd)
                } else {
                    extract_project_from_path(path)
                };

                let session_date = chrono::DateTime::parse_from_rfc3339(timestamp)
                    .with_context(|| format!("Failed to parse timestamp: {}", timestamp))?
                    .with_timezone(&Utc);

                return Ok((project_name, session_date));
            }
            ClaudeCodeLogEntry::Assistant { timestamp, .. } => {
                let project_name = extract_project_from_path(path);

                let session_date = chrono::DateTime::parse_from_rfc3339(timestamp)
                    .with_context(|| format!("Failed to parse timestamp: {}", timestamp))?
                    .with_timezone(&Utc);

                return Ok((project_name, session_date));
            }
            _ => {}
        }
    }

    let project_name = extract_project_from_path(path);
    Ok((project_name, Utc::now()))
}

fn extract_project_name(cwd: &str) -> String {
    std::path::Path::new(cwd)
        .file_name()
        .and_then(|n| n.to_str())
        .unwrap_or("unknown")
        .to_string()
}

fn extract_project_from_path(path: &Path) -> String {
    let components: Vec<_> = path.components().collect();

    for (i, comp) in components.iter().enumerate() {
        if comp.as_os_str() == "projects" && i + 1 < components.len() {
            if let Some(name) = components[i + 1].as_os_str().to_str() {
                return name.to_string();
            }
        }
    }

    path.file_stem()
        .and_then(|n| n.to_str())
        .unwrap_or("unknown")
        .to_string()
}

fn extract_argument_summary(tool_name: &str, input: &serde_json::Value) -> String {
    match tool_name {
        "Bash" => {
            if let Some(cmd) = input.get("command").and_then(|v| v.as_str()) {
                return cmd.to_string();
            }
            "unknown".to_string()
        }
        "Read" => {
            if let Some(path) = input.get("file_path").and_then(|v| v.as_str()) {
                return format!("Read: {}", shorten_path(path));
            }
            "unknown".to_string()
        }
        "Write" => {
            if let Some(path) = input.get("file_path").and_then(|v| v.as_str()) {
                return format!("Write: {}", shorten_path(path));
            }
            "unknown".to_string()
        }
        "Edit" => {
            if let Some(path) = input.get("file_path").and_then(|v| v.as_str()) {
                return format!("Edit: {}", shorten_path(path));
            }
            "unknown".to_string()
        }
        "Grep" => {
            if let Some(pattern) = input.get("pattern").and_then(|v| v.as_str()) {
                return format!("Grep: {}", pattern);
            }
            "unknown".to_string()
        }
        "Glob" => {
            if let Some(pattern) = input.get("pattern").and_then(|v| v.as_str()) {
                return format!("Glob: {}", pattern);
            }
            "unknown".to_string()
        }
        _ => {
            let input_str = input.to_string();
            let char_count = input_str.chars().count();
            if char_count > 50 {
                let truncated: String = input_str.chars().take(50).collect();
                format!("{}...", truncated)
            } else {
                input_str
            }
        }
    }
}

fn shorten_path(path: &str) -> String {
    let path = std::path::Path::new(path);
    if let Some(file_name) = path.file_name() {
        file_name.to_string_lossy().to_string()
    } else {
        path.to_string_lossy().to_string()
    }
}

fn extract_bash_command(input: &serde_json::Value) -> Option<String> {
    input
        .get("command")
        .and_then(|v| v.as_str())
        .map(|s| s.to_string())
}

