    )
    parser.add_argument(
        "--top",
        type=int,
        default=20,
        help="Number of ranked rows to display per table (default: 20).",
    )
    parser.add_argument(
        "--show-full",
        action="store_true",
        help="Display all rows instead of limiting to --top entries.",
    )
    args = parser.parse_args()

    sessions_root = Path(args.sessions_root)
