    """Yield all JSONL session files under the given directory sorted by name."""
    if not root.exists():
        message = f"sessions directory not found: {root}"
        raise FileNotFoundError(message)
    for path in sorted(root.rglob("*.jsonl")):
        if path.is_file():
            yield path


